        // [ "كَتَبْتُ إِلى أَبِي جَعْفَرٍ عليه‌السلام", 5 ],
        // [ "عَنْ أَبِي عَبْدِ اللهِ عليه‌السلام", 6 ],
        // [ "سَأَلْتُ أَبَا عَبْدِ اللهِ عليه‌السلام", 6 ],
        // [ ": عن أبي جعفر عليه‌السلام", 5 ],
