export function init () {

    let PBox: TS.db = [];
    for ( let p of ref_box ) {
        if ( typeof p.a === "object" ) 
            if ( !PBox.find( x => x.n === (p.a as any).n ) )
                PBox.push( p.a as any );
        if ( typeof p.b === "object" ) 
            if ( !PBox.find( x => x.n === (p.b as any).n ) )
                PBox.push( p.b as any );
    }
    for ( let p of PBox ) { 
        delete ( p as any ).x;
        p.old_n = p.n;
    }


    let mmm = [ ...server001.db, ...server002.db, ...server003.db ];
    let R: TS.R[];

    // .. update box
    tools.n_allocation( PBox , 90001 );

    R = __.R_R( PBox, mmm );
    R = tools.R_optimizer( R, 67 );

    let clusterBox: number[][] = [];
    for ( let p of PBox ) clusterBox.push( tools.cluster( p.n, R ) );

    clusterBox = clusterBox.map( x => {
        if ( x.length === 1 ) return x;
        else {
            let picked = -1;
            for ( let y of x.slice(1) ) {
                if ( mmm.find( z => z.n === y ).cDB ) {
                  if ( picked !== -1 ) console.log( "DOUBLE:" , picked, y );
                    picked = y;
                }
            }
            return [ x[0], picked ];
        }
    } )

    for ( let i in PBox ) {
        let item = clusterBox.find( x => x[0] === PBox[i].n );
        if ( item ) {
            let regx = new RegExp( "H_" + PBox[i].old_n, "g" );
            bound_str = bound_str.replace( regx, "HW_" + item[ item.length - 1 ] );
            if ( item.length > 1 ) delete PBox[i];
        }
    }

    bound_str = bound_str.replace( /HW_/g, "H_" );
    PBox = tools.dbCleaner( PBox.filter( x => x ) );
    for ( let i in PBox ) delete PBox[i].old_n;

    fs.writeFileSync( tmpFolder + "bind.json", bound_str );
    storage.saveData( PBox, "db/ready", "نهاية" );

}

